<template>
    <div class="main">
        <b-container>
            <b-row>
                <div class="col-lg-1">
                    <router-link to="/" tag="div" class="logo">
                        <img src="@/assets/images/logo.jpg" alt="">
                    </router-link>
                </div>
                <div class="col-lg-3 text-center">
                    <span>Телефон доставки г.Алматы</span><br>
                    <span>+7 (777) 777 77 77</span><br>
                    <span>+7 (777) 777 77 77</span>
                </div>
                <div class="col-lg-6">
                    <b-input-group class="cart">
                        <b-form-input v-model="search" type="text" placeholder="Что ищем?"></b-form-input>
                        <b-input-group-append size="sm">
                            <b-button type="submit" @click="searching()" size="sm">
                                <svg width="13" height="13" viewBox="0 0 13 13" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                                    <rect width="13" height="13" fill="url(#pattern0)"/>
                                    <defs>
                                    <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
                                    <use xlink:href="#image0" transform="scale(0.00195312)"/>
                                    </pattern>
                                    <image id="image0" width="512" height="512" xlink:href="data:image/png;base64,"/>
                                    </defs>
                                </svg>
                            </b-button>
                        </b-input-group-append>
                    </b-input-group>
                </div>
                <div class="col-lg-2">
                    <router-link style="cursor: pointer;" to="/basket" tag="div" class="basket-block">
                        <div class="basket">
                            <img src="@/assets/images/cart1.jpg" alt="">
                            <p>
                                <span v-if="AUTH">{{COUNT}} </span>
                                <span v-if="!AUTH">{{LOCAL_COUNT}} </span>
                                товаров
                            </p>
                            <svg width="9" height="10" viewBox="0 0 9 10" fill="none" xmlns="http://www.w3.org/2000/svg">
                                <path d="M4.19995 1.39996L7.19984 5.39993L4.19995 9.39996" stroke="#4E54CA"/>
                            </svg>
                        </div>
                    </router-link>
                </div>
            </b-row>
        </b-container> 
    </div>
</template>

<script>
    import { mapGetters } from 'vuex'
    export default {
        data() {
            return {
                search: ''
            }
        },
        computed: {
            ...mapGetters([
                'COUNT',
                'LOCAL_COUNT',
                'AUTH'
            ])
        },
        methods: {
            searching() {
                this.$router.push({name: 'search', params: {text: this.search}});
                this.search = ''
            }
        },
        created () {
            this.$store.dispatch('GET_COUNT');
            this.$store.dispatch('GET_LOCAL_COUNT');
        },
    }
</script>

<style scoped>
    .main{
        font-size: 12px;
        border-bottom: 1px solid #C4C4C4;
        padding: 5px 0;
    }
    .main .row{
        display: flex;
        align-items: center;
    }
    input, button{
        background: #E8E8E8;
        border: none;
    }
    button{
        padding: 0 10px;
    }
    input{
        padding-left: 20px;
    }
    .basket-block{
        border: 1px solid #C4C4C4;
        padding: 5px;
    }
    .basket{
        display: flex;
        align-items: center;
    }
    .basket p{
        margin: 0 10px;
        padding: 5px 0;
        color: #4E54CA;
        font-size: 12px;
    }
    .img{
        cursor: pointer;
    }
    .logo{
        cursor: pointer;
    }
    .logo img{
        width: 50px;
    }
    .basket img{
        width: 40px;
    }
</style>